sourcesContent:
import userService from '../service/userService'
import bookingService from '../service/bookingService'
import feedbackService from '../service/feedbackService'
import roomService from '../service/roomService'; 


import bcrypt from 'bcryptjs';
const db = require('../config/connectDB');


const login = (req, res) => {
    return res.render("login.ejs");
};

const handleLogin = async (req, res) => {
    const { username, password } = req.body;
    console.log("MSSV/Email:", username);
    console.log("Password:", password);

    if (!username || !password) {
        return res.render("login.ejs", { error: "Vui lng nhp y  thng tin!" });
    }

    const user = await userService.findUserByMSSVOrEmail(username);
    if (!user) {
        return res.render("login.ejs", { error: "Khng tm thy ti khon!" });
    }

    let match = false;

    try {
        match = await bcrypt.compare(password, user.Pass);
    } catch (err) {
        console.error("Li so snh bcrypt:", err);
    }

    if (!match) {
        match = password === user.Pass;
    }

    if (!match) {
        return res.render("login.ejs", { error: "Sai mt khu!" });
    }

    try {
        await userService.updateUserStatus(user.mssv, 'Online'); 
    } catch (err) {
        console.error("Li khi cp nht trng thi:", err);
    }

    req.session.loggedin = true;
    req.session.username = user.fullName;
    req.session.userId = user.mssv; 
    res.redirect("/home");
};

const logout = (req, res) => {
    const userId = req.session.userId;

    userService.updateUserStatus(userId, 'Offline').then(() => {
        req.session.destroy(err => {
            if (err) {
                console.log("Li", err);
                return res.status(500);
            }
            res.clearCookie("connect.sid");
            res.redirect("/"); 
        });
    }).catch((err) => {
        console.error('Li khi cp nht trng thi khi ng xut:', err);
        res.status(500).send('Li khi ng xut');
    });
};


const updateAccount = async (req, res) => {
    const { mssv, fullName, email, phone, sex, role, pass } = req.body;
  
    try {
      await userService.updateUser(mssv, fullName, email, phone, sex, role, pass);
      res.redirect("/home");
    } catch (error) {
      console.error('Li khi cp nht ti khon:', error);
      res.status(500).send('Li khi cp nht ti khon');
    }
  };  

const home = async (req, res) => {
    if (req.session.loggedin) {
        try {
            const accounts = await userService.getUserList(); 
            const bookings = await bookingService.getBookingList();
            const feedback = await feedbackService.getFeedbackList();
            const rooms = await roomService.getRoomList();

            return res.render("home.ejs", {
                username: req.session.username,
                accounts: accounts,
                bookings: bookings,
                mssvLogin: req.session.userId,
                feedback: feedback,
                rooms: rooms
            });
        } catch (error) {
            console.log("Error fetching user accounts:", error);
            return res.status(500).send("Server error!");
        }
    } else {
        return res.redirect("/");
    }
};


const account = async (req, res) => {
    const { mssv, fullName, email, pass, phone, sex, role } = req.body;
    console.log("Account data:", req.body);
    await userService.createNewUser(mssv, fullName, email, pass, phone, sex, role);
    res.redirect("/home");
  };
  
  const deleteAccount = async (req, res) => {
    const { mssv } = req.params;
    if (mssv === req.session.userId) {
      return res.status(400).send("Khng th xa chnh mnh.");
    }
    try {
      await userService.deleteUser(mssv);
      res.redirect("/home");
    } catch (error) {
      console.error("Li khi xa ngi dng:", error);
      res.status(500).send("Li khi xa ngi dng");
    }
  };
  

module.exports = {
    login,
    handleLogin,
    home,
    account,
    logout,
    updateAccount,
    deleteAccount
};

