import roomService from "../service/roomService"; // m bo ng dn ng

/**
 * Controller  to phng mi (thng t form submission).
 * Nhn d liu t req.body, gi service v redirect hoc gi li.
 */
const createRoom = async (req, res) => {
    try {
        // 1. Nhn d liu t req.body (bao gm c cc trng mi)
        const roomData = {
            capacity: parseInt(req.body.capacity, 10), // Chuyn sang s
            location: req.body.location,
            room_status: req.body.room_status || 'Available',
            devices: req.body.devices, // Chui hoc undefined/null
            // Nhn trng mi, chuyn i 'available_seats' sang s
            // Nu available_seats trng, service s t t bng capacity
            available_seats: req.body.available_seats ? parseInt(req.body.available_seats, 10) : undefined,
            room_type: req.body.room_type // 'single' hoc 'group'
        };

        console.log("BE Controller: Received data for new room:", roomData);

        // 2. Validation c bn  Controller (Service cng c validation chi tit hn)
        if (isNaN(roomData.capacity) || roomData.capacity <= 0) {
             throw new Error("Sc cha khng hp l.");
        }
         if (roomData.available_seats !== undefined && isNaN(roomData.available_seats)) {
              throw new Error("S ch trng phi l mt s.");
         }
         // Service s validate tip room_type, status, location, etc.


        // 3. Gi Service
        const result = await roomService.addRoom(roomData);
        console.log("BE Controller: Room added successfully:", result);

        // 4. Redirect sau khi thnh cng (ph hp vi lung form submission)
        // Nu mun gi JSON th thay bng: res.status(201).json({ success: true, ...result });
        // Cn nhc dng connect-flash  gi thng bo thnh cng qua redirect
        // req.flash('success', result.message);
        res.redirect("/home"); // Hoc trang danh sch phng

    } catch (error) {
        // 5. X l li
        console.error("BE Controller: Error creating room:", error);
        // Thay v gi JSON, gi thng bo li hoc redirect v form vi li
        // Nu dng connect-flash:
        // req.flash('error', error.message || "Li xy ra khi to phng.");
        // res.redirect('/page-with-add-form'); // Redirect v trang c form
        // Hoc hin th trang li n gin:
        res.status(400).send(`
            <html><body>
                <h2>Li khi to phng</h2>
                <p>${error.message || " c li xy ra."}</p>
                <a href="/home">Quay li trang ch</a>
                <script>setTimeout(() => window.history.back(), 3000);</script> <%# T ng quay li sau 3s %>
            </body></html>
        `);
    }
};

/**
 * Controller  ly danh sch tt c phng (thng dng cho API).
 * Tr v d liu JSON.
 */
const getAllRooms = async (req, res) => {
    try {
        const rooms = await roomService.getRoomList(); // Service  c cp nht
        res.status(200).json({ success: true, data: rooms }); // Lun tr v cu trc nht qun
    } catch (error) {
        console.error("BE Controller: Error fetching all rooms:", error);
        res.status(500).json({ success: false, message: error.message || "Li ly danh sch phng." });
    }
};

/**
 * Controller  ly chi tit mt phng (thng dng cho API).
 * Tr v d liu JSON.
 */
const getRoom = async (req, res) => {
    try {
        const roomId = parseInt(req.params.id, 10); // Ly ID t URL v chuyn sang s
        if (isNaN(roomId)) {
             return res.status(400).json({ success: false, message: "ID phng khng hp l." });
        }
        const room = await roomService.getRoomById(roomId); // Service  c cp nht
        if (!room) {
            return res.status(404).json({ success: false, message: "Khng tm thy phng" });
        }
        res.status(200).json({ success: true, data: room });
    } catch (error) {
        console.error("BE Controller: Error fetching room details:", error);
        res.status(500).json({ success: false, message: error.message || "Li ly chi tit phng." });
    }
};

/**
 * Controller  cp nht phng (thng t form submission).
 * Nhn d liu t req.body, gi service v redirect hoc gi li.
 */
const editRoom = async (req, res) => {
    try {
      const roomId = parseInt(req.body.room_id, 10); // Ly ID t body v chuyn sang s
       if (isNaN(roomId)) {
             throw new Error("ID phng khng hp l  cp nht.");
       }

      // 1. To i tng cha d liu cp nht t form
      const updatedRoomData = {
        capacity: req.body.capacity ? parseInt(req.body.capacity, 10) : undefined,
        location: req.body.location || undefined, // Gi li nu c, undefined nu rng
        status: req.body.room_status || undefined,
        // devices: req.body.devices, // Cp nht devices phc tp, tm thi b qua  y
        available_seats: req.body.available_seats !== undefined && req.body.available_seats !== ''
                         ? parseInt(req.body.available_seats, 10)
                         : undefined, // undefined nu trng
        room_type: req.body.room_type || undefined
      };

        console.log(`BE Controller: Received update data for room ${roomId}:`, updatedRoomData);

      // 2. Loi b cc trng khng c gi tr (undefined)  service khng cp nht chng
      Object.keys(updatedRoomData).forEach(key => updatedRoomData[key] === undefined && delete updatedRoomData[key]);

       // 3. Kim tra xem c d liu thc s  cp nht khng
       if (Object.keys(updatedRoomData).length === 0) {
             console.log(`BE Controller: No valid fields to update for room ${roomId}.`);
             // req.flash('info', 'Khng c thng tin no c thay i.');
             return res.redirect("/home"); // Khng c g  lm, quay v home
       }

       // 4. Validation cc gi tr s sau khi parse
       if (updatedRoomData.capacity !== undefined && isNaN(updatedRoomData.capacity)) throw new Error("Sc cha khng hp l.");
       if (updatedRoomData.available_seats !== undefined && isNaN(updatedRoomData.available_seats)) throw new Error("S ch trng khng hp l.");


      // 5. Gi Service (Service  c cp nht  ch update cc trng c cung cp)
      const updatedRoom = await roomService.updateRoom(roomId, updatedRoomData);

      if (!updatedRoom) {
         // Service tr v null nu phng khng tn ti
         throw new Error("Khng tm thy phng  cp nht.");
      }
      console.log(`BE Controller: Room ${roomId} updated successfully.`);

      // 6. Redirect sau khi thnh cng
      // req.flash('success', 'Cp nht phng thnh cng!');
      res.redirect("/home");

    } catch (error) {
      // 7. X l li
      console.error(`BE Controller: Error updating room ${req.body.room_id}:`, error);
      // req.flash('error', error.message || "Li khi cp nht phng.");
      // res.redirect('/home'); // Hoc redirect v trang edit
        res.status(400).send(`
            <html><body>
                <h2>Li khi cp nht phng</h2>
                <p>${error.message || " c li xy ra."}</p>
                <a href="/home">Quay li trang ch</a>
                <script>setTimeout(() => window.history.back(), 3000);</script>
            </body></html>
        `);
    }
  };


/**
 * Controller  kha phng (chuyn status sang Maintenance - thng t form/button).
 * Nhn ID t URL params, gi service v redirect hoc gi li.
 */
const lockRoom = async (req, res) => {
    try {
        const roomId = parseInt(req.params.id, 10); // Ly ID t URL
         if (isNaN(roomId)) {
             throw new Error("ID phng khng hp l  kha.");
         }

        // Gi service (service tr v true nu thnh cng, false nu khng tm thy/ kha)
        const lockSuccess = await roomService.lockRoom(roomId);

        if (!lockSuccess) {
             // req.flash('warning', `Khng th kha phng ${roomId}. Phng khng tn ti hoc   trng thi bo tr.`);
            // Thay v li 404 JSON, thng bo v redirect
            return res.status(404).send(`
                <html><body>
                    <h2>Thng bo</h2>
                    <p>Khng th kha phng ${roomId}. Phng khng tn ti hoc   trng thi bo tr.</p>
                    <a href="/home">Quay li trang ch</a>
                    <script>setTimeout(() => window.history.back(), 3000);</script>
                </body></html>
            `);
        }
        console.log(`BE Controller: Room ${roomId} locked successfully.`);
        // req.flash('success', `Phng ${roomId}  c chuyn sang trng thi Bo tr.`);
        res.redirect("/home"); // Redirect thnh cng

    } catch (error) {
        console.error(`BE Controller: Error locking room ${req.params.id}:`, error);
        // req.flash('error', error.message || "Li xy ra khi kha phng.");
        // res.redirect('/home');
        res.status(500).send(`
            <html><body>
                <h2>Li khi kha phng</h2>
                <p>${error.message || " c li xy ra."}</p>
                <a href="/home">Quay li trang ch</a>
                <script>setTimeout(() => window.history.back(), 3000);</script>
            </body></html>
        `);
    }
};

/**
 * Controller  ly danh sch phng trng (thng dng cho API).
 * Tr v d liu JSON.
 */
const listAvailableRooms = async (req, res) => {
    try {
        const availableRooms = await roomService.getAvailableRooms(); // Service  c cp nht
        res.status(200).json({
            success: true,
            message: "Ly danh sch phng trng thnh cng",
            data: availableRooms
        });
    } catch (error) {
        console.error("BE Controller: Error fetching available rooms:", error);
        res.status(500).json({
            success: false,
            message: "Li my ch khi ly danh sch phng trng",
            error: error.message || "Unknown error" // Cung cp thng tin li r rng hn
        });
    }
};

// Export tt c cc hm controller
export default {
    createRoom,         // X l form POST -> Redirect
    getAllRooms,        // API -> JSON
    getRoom,            // API -> JSON
    editRoom,           // X l form POST -> Redirect
    lockRoom,           // X l form POST -> Redirect
    listAvailableRooms  // API -> JSON
};

