import bookingService from '../service/bookingService';

function combineDateTime(dateString, timeString) {
    if (!dateString || !timeString || !/^\d{4}-\d{2}-\d{2}$/.test(dateString) || !/^\d{2}:\d{2}$/.test(timeString)) {
        return null; // Tr v null nu nh dng sai
    }
    // Gi nh client gi ng nh dng gi, thm giy ':00'
    return `${dateString} ${timeString}:00`;
}

const createBooking = async (req, res) => {
    try {
        const loggedInUser = req.user;
        console.log("Payload received in createBooking (req.user):", loggedInUser);

        // Kim tra s tn ti ca loggedInUser v trng mssv
        if (!loggedInUser || !loggedInUser.mssv) {
             console.error("Authentication check failed: Missing user or mssv in token payload.");
             return res.status(401).json({ success: false, message: "Yu cu ng nhp hoc token khng cha  thng tin." });
        }

        // Nhn d liu t client
        const { room_id, date, start_time, end_time, number_of_attendees } = req.body;

        // Validation u vo c bn
        if (!room_id || !date || !start_time || !end_time) {
            return res.status(400).json({ success: false, message: "Thiu thng tin phng, ngy hoc thi gian." });
        }
        if (!/^\d{4}-\d{2}-\d{2}$/.test(date) || !/^\d{2}:\d{2}(:\d{2})?$/.test(start_time) || !/^\d{2}:\d{2}(:\d{2})?$/.test(end_time)) {
            return res.status(400).json({ success: false, message: "nh dng ngy (YYYY-MM-DD) hoc gi (HH:MM hoc HH:MM:SS) khng hp l." });
        }

        // Chun b d liu cho service THEO NG YU CU CA SERVICE MI
        const bookingData = {
            room_id: parseInt(room_id, 10),
            mssv: loggedInUser.mssv,      // <-- GI MSSV
            date: date,                   // <-- GI NGY RING
            start_time: start_time,       // <-- GI GI BT U RING
            end_time: end_time,           // <-- GI GI KT THC RING
            number_of_attendees: number_of_attendees ? parseInt(number_of_attendees, 10) : undefined
            // KHNG GI user_id na
        };

        // Validation kiu s
         if (isNaN(bookingData.room_id)) {
              return res.status(400).json({ success: false, message: "ID phng khng hp l." });
         }
         if (bookingData.number_of_attendees !== undefined && isNaN(bookingData.number_of_attendees)) {
              return res.status(400).json({ success: false, message: "S lng ngi tham gia khng hp l." });
         }

        console.log('BE Controller: Sending data to service:', bookingData); // Log d liu s gi i

        // Gi service vi d liu  chun b
        const result = await bookingService.addBooking(bookingData); // Service addBooking phi c cp nht  nhn cc trng ny

        // Tr v kt qu thnh cng
        return res.status(201).json({
            success: true,
            message: result.message,
            bookingId: result.bookingId
        });

    } catch (error) {
        // X l li tp trung
        console.error("Controller Error: Creating booking:", error);
        let statusCode = 500;
        const errorMessage = error.message || "Li khng xc nh khi x l t phng.";

        if (errorMessage.includes("Thiu thng tin") || errorMessage.includes("khng hp l") || errorMessage.includes("Vui lng nhp s lng")) {
            statusCode = 400;
        } else if (errorMessage.includes("vt qu sc cha")) {
            statusCode = 400;
        } else if (errorMessage.includes("Khng tm thy phng") || errorMessage.includes("Phng hoc ngi dng khng tn ti")) {
            statusCode = 404;
        } else if (errorMessage.includes(" c ngi t") || errorMessage.includes("trng khp")) {
            statusCode = 409;
        } else if (errorMessage.includes("Yu cu ng nhp")) {
             statusCode = 401;
        }

        return res.status(statusCode).json({
            success: false,
            message: errorMessage
        });
    }
};

// Controller checkAvailability cng cn cp nht tng t
const checkAvailability = async (req, res) => {
     try {
         // Nhn ngy, gi ring
         const { room_id, date, start_time, end_time } = req.body;

         if (!room_id || !date || !start_time || !end_time) {
             return res.status(400).json({ success: false, message: "Thiu thng tin phng, ngy hoc thi gian." });
         }

         // Kt hp ngy gi
         const fullStartTime = combineDateTime(date, start_time);
         const fullEndTime = combineDateTime(date, end_time);

         if (!fullStartTime || !fullEndTime) {
             return res.status(400).json({ success: false, message: "nh dng ngy hoc gi khng hp l." });
         }

         const roomIdNum = parseInt(room_id, 10);
         if (isNaN(roomIdNum)) {
              return res.status(400).json({ success: false, message: "ID phng khng hp l." });
         }

         // Gi service vi d liu  kt hp
         const result = await bookingService.checkBookingTimeSlot(roomIdNum, fullStartTime, fullEndTime);
         return res.status(200).json({ success: true, available: result.available, message: result.message });

     } catch (error) {
          console.error("Controller Error: Checking availability:", error);
          res.status(500).json({ success: false, available: false, message: error.message || "Li my ch." });
     }
 };
 const handleCancelBooking = async (req, res) => {
    // t tn  d log li (nu cn)
    const functionName = 'handleCancelBooking';
    try {
        // 1. Ly bookingId t tham s trn URL (v d: /api/bookings/123/cancel)
        const { bookingId } = req.params;

        // 2. Validate d liu u vo (Rt quan trng!)
        if (!bookingId) {
            console.error(`[${functionName}] Li: Thiu bookingId trong request.`);
            return res.status(400).json({ // 400 Bad Request
                success: false,
                message: "Vui lng cung cp Booking ID."
            });
        }

        const bookingIdInt = parseInt(bookingId, 10); // Chuyn sang s nguyn
        if (isNaN(bookingIdInt)) {
            console.error(`[${functionName}] Li: bookingId khng phi l s hp l:`, bookingId);
            return res.status(400).json({ // 400 Bad Request
                success: false,
                message: "Booking ID khng hp l."
            });
        }

        console.log(`[${functionName}] Nhn yu cu hy booking vi ID: ${bookingIdInt}`);

        // 3. Gi hm service  thc hin logic hy
        // Hm service s x l transaction v tr v message hoc nm li
        const result = await bookingService.cancelBooking(bookingIdInt);

        // 4. Nu service thnh cng (khng nm li), gi phn hi thnh cng
        console.log(`[${functionName}]  hy thnh cng booking ID: ${bookingIdInt}`);
        return res.status(200).json({ // 200 OK
            success: true,
            message: result.message // Ly thng bo t service
        });

    } catch (error) {
        // 5. Nu c li xy ra trong qu trnh x l (t service hoc li khc)
        console.error(`[${functionName}] Li khi x l hy booking:`, error);

        // Xc nh m trng thi HTTP ph hp da trn li
        let statusCode = 500; // Mc nh l li server
        const errorMessage = error.message || " xy ra li khng mong mun khi hy t phng.";

        // Phn tch cc li c th t service  tr v m li chnh xc hn
        if (errorMessage.includes("Khng tm thy t phng") || errorMessage.includes("khng tn ti")) {
            statusCode = 404; // 404 Not Found
        } else if (errorMessage.includes("Khng th hy") || errorMessage.includes("Ch hy c")) {
            // L do nghip v khng cho php hy (v d: sai trng thi)
            statusCode = 400; // 400 Bad Request (hoc 409 Conflict ty ng cnh)
        } else if (errorMessage.includes("khng hp l")) { // T bc validate  trn
             statusCode = 400; // 400 Bad Request
        }
        // Cc li khc (li DB, li transaction...) thng l 500

        return res.status(statusCode).json({
            success: false,
            message: errorMessage
        });
    }
};
const handleBookNow = async (req, res) => {
    const functionName = 'handleBookNow';
    try {
        const mssv = req.user?.mssv;
        if (!mssv) {
            console.error(`[${functionName}] Li: Khng tm thy thng tin ngi dng (MSSV)  xc thc.`);
            return res.status(401).json({ success: false, message: "Yu cu xc thc khng hp l." });
        }

        const { roomId, duration_minutes, number_of_attendees } = req.body;

        if (!roomId) {
             return res.status(400).json({ success: false, message: "Vui lng cung cp Room ID." });
        }

        console.log(`[${functionName}] User ${mssv} requests to book now room ${roomId}`);

        const result = await bookingService.bookNow({
            roomId,
            mssv,
            duration_minutes,
            number_of_attendees
        });

        return res.status(200).json({
            success: true,
            message: result.message,
            data: {
                bookingId: result.bookingId,
                startTime: result.startTime,
                endTime: result.endTime,
                date: result.date
            }
        });

    } catch (error) {
        console.error(`[${functionName}] Controller Error:`, error);
        let statusCode = 500;
        const errorMessage = error.message || "Li khng xc nh khi t phng ngay.";

        if (errorMessage.includes("khng hp l") || errorMessage.includes("Thiu thng tin")) {
            statusCode = 400;
        } else if (errorMessage.includes("Khng tm thy phng")) {
            statusCode = 404;
        } else if (errorMessage.includes("khng trng") || errorMessage.includes("Khng  ch") || errorMessage.includes(" c lch t khc")) {
            statusCode = 409;
        }

        return res.status(statusCode).json({
            success: false,
            message: errorMessage
        });
    }
};

export default {
    createBooking,
    checkAvailability,handleCancelBooking,handleBookNow
};

