sourcesContent:
import jwt from 'jsonwebtoken';

const JWT_SECRET = "your_secret_key";
const authenticateToken = (req, res, next) => {
    const authHeader = req.headers['authorization'];
    const token = authHeader && authHeader.startsWith('Bearer ') ? authHeader.split(' ')[1] : null;

    if (token == null) {
        // Khng tm thy token
        return res.status(401).json({ success: false, message: 'Yu cu xc thc. Vui lng ng nhp.' });
    }

    // Xc minh token bng kha b mt  nh ngha  trn
    jwt.verify(token, JWT_SECRET, (err, decodedPayload) => {
        if (err) {
            // X l li xc minh
            console.error("Auth Middleware: Token verification failed:", err.message);
            if (err.name === 'TokenExpiredError') {
                 return res.status(401).json({ success: false, message: 'Phin ng nhp  ht hn. Vui lng ng nhp li.' });
            }
            // Li khc (vd: ch k sai do key khng khp) -> 403 Forbidden
            return res.status(403).json({ success: false, message: 'Token khng hp l hoc b t chi.' });
        }

        // Token hp l, gn payload (cha mssv, role) vo req.user
        req.user = decodedPayload;
        next(); // Cho php i tip
    });
};

export default authenticateToken;

