// src/service/bookingService.js
import connection from "../config/connectDB";
import { format } from 'date-fns';


// --- Updated Constants ---
const DEFAULT_BOOKING_DURATION_MINUTES = 60; // Default for single or if not specified otherwise
const DEFAULT_DURATION_QUICK_BOOK_MINUTES = 120; // Default duration for quick booking GROUP rooms (2 hours)
const DEFAULT_GROUP_ATTENDEES = 6; // Default attendees for quick booking GROUP rooms
const DEFAULT_SINGLE_ATTENDEES = 1; // Default attendees for quick booking 

/**
 * Ly danh sch tt c cc t phng km thng tin phng.
 */
const getBookingList = () => {
    return new Promise((resolve, reject) => {
        const sql = `
            SELECT
                b.id AS id,
                b.mssv,
                b.Day,
                DATE_FORMAT(b.start_time, '%H:%i') AS start_time,
                DATE_FORMAT(b.end_time, '%H:%i') AS end_time,
                b.status,
                r.location AS room_name,
                r.qr_code AS qr_code,
                b.booked_seats -- Ly thm s ch  t
            FROM
                Bookings b
            JOIN
                Rooms r ON b.room_id = r.ID
            ORDER BY
                b.Day DESC, b.start_time ASC
        `;
        connection.query(sql, (err, results) => {
            if (err) {
                console.error("Database error fetching booking list:", err);
                return reject(new Error("Li truy vn c s d liu khi ly danh sch t ch."));
            }
            resolve(results);
        });
    });
};

/**
 * Kim tra xem khung gi  c lt t no khc  xc nhn ('Confirmed')
 * chim dng hay cha.
 */
const checkBookingTimeSlot = (roomId, date, startTime, endTime) => {
    return new Promise((resolve, reject) => {
        const overlapQuery = `
            SELECT COUNT(*) AS conflict_count
            FROM Bookings
            WHERE room_id = ?
              AND Day = ?
              AND status = 'Confirmed' -- Ch kim tra trng thi Confirmed
              AND start_time < ?
              AND end_time > ?
        `;
        const values = [roomId, date, endTime, startTime];
        connection.query(overlapQuery, values, (err, results) => {
            if (err) {
                console.error(`Service Error: Checking time slot for room ${roomId} on ${date}:`, err);
                return reject(new Error("Li kim tra lch t phng."));
            }
            const isAvailable = results[0].conflict_count === 0;
            resolve({
                available: isAvailable,
                message: isAvailable ? "Khung thi gian cn trng." : "Khung thi gian  c ngi t."
            });
        });
    });
};

/**
 * Thm mt t phng mi vi trng thi 'Confirmed', lu s ch  t,
 * v cp nht s ch trng ca phng.
 * @param {object} bookingData - D liu t phng.
 * @param {number} bookingData.room_id
 * @param {string} bookingData.mssv
 * @param {string} bookingData.date
 * @param {string} bookingData.start_time
 * @param {string} bookingData.end_time
 * @param {number} [bookingData.number_of_attendees]
 * @returns {Promise<{bookingId: number, message: string}>}
 */
const addBooking = (bookingData) => {
    return new Promise((resolve, reject) => {
        connection.beginTransaction(async (transactionErr) => {
            if (transactionErr) {
                console.error("Transaction Error: Begin failed:", transactionErr);
                return reject(new Error("Khng th bt u giao dch c s d liu."));
            }

            try {
                const { room_id, mssv, date, start_time, end_time, number_of_attendees } = bookingData;

                // 1. Basic Validations
                if (!room_id || !mssv || !date || !start_time || !end_time) throw new Error("Thiu thng tin bt buc.");
                if (start_time >= end_time) throw new Error("Thi gian bt u phi trc thi gian kt thc.");
                const roomIdInt = parseInt(room_id, 10);
                if (isNaN(roomIdInt)) throw new Error("room_id khng hp l.");

                // 2. Fetch Room Info & Lock Row
                const roomInfo = await new Promise((res, rej) => {
                    const query = 'SELECT ID, capacity, location, status, available_seats, room_type FROM Rooms WHERE ID = ? FOR UPDATE';
                    connection.query(query, [roomIdInt], (err, results) => {
                        if (err) return rej(new Error("Li truy vn phng: " + err.message));
                        if (results.length === 0) return rej(new Error(`Khng tm thy phng ID: ${roomIdInt}.`));
                        res(results[0]);
                    });
                });

                // 3. Check Room Status (must be Available)
                if (roomInfo.status !== 'Available') {
                    throw new Error(`Phng ${roomInfo.location} khng kh dng (Trng thi: ${roomInfo.status}).`);
                }

                // 4. Determine booked_seats & Validate Attendees/Capacity
                let seatsToDecrement = 0; // S ch s tr i v lu vo booked_seats
                if (roomInfo.room_type === 'single') {
                    seatsToDecrement = 1;
                } else if (roomInfo.room_type === 'group') {
                    const requestedAttendees = parseInt(number_of_attendees, 10);
                    if (isNaN(requestedAttendees) || requestedAttendees <= 0) throw new Error("S lng ngi tham gia khng hp l cho phng nhm.");
                    if (requestedAttendees > roomInfo.capacity) throw new Error(`S lng (${requestedAttendees}) vt qu sc cha (${roomInfo.capacity}).`);
                    seatsToDecrement = requestedAttendees;
                } else {
                    throw new Error(`Loi phng khng xc nh: ${roomInfo.room_type}`);
                }

                // 5. Check Available Seats
                if (roomInfo.available_seats < seatsToDecrement) {
                    throw new Error(`Khng  ch. Phng ${roomInfo.location} cn ${roomInfo.available_seats} ch (cn ${seatsToDecrement}).`);
                }

                // 6. Check Time Slot Conflict
                const timeSlotCheck = await checkBookingTimeSlot(roomIdInt, date, start_time, end_time);
                if (!timeSlotCheck.available) {
                    throw new Error(timeSlotCheck.message);
                }

                // 7. Insert Booking Record ('Confirmed', booked_seats)
                const bookingResult = await new Promise((res, rej) => {
                    const query = `INSERT INTO Bookings (room_id, mssv, Day, start_time, end_time, status, booked_seats) VALUES (?, ?, ?, ?, ?, ?, ?)`;
                    const values = [roomIdInt, mssv, date, start_time, end_time, 'Confirmed', seatsToDecrement];
                    connection.query(query, values, (err, result) => {
                        if (err) {
                             if (err.code === 'ER_DUP_ENTRY') return rej(new Error("Li: t phng ny c v  tn ti."));
                             return rej(new Error("Li DB khi to t phng: " + err.message));
                        }
                        if (!result || !result.insertId) return rej(new Error("Khng th to t phng."));
                        res(result);
                    });
                });

                // 8. Update Room Seats and Status
                const newAvailableSeats = roomInfo.available_seats - seatsToDecrement;
                const newStatus = newAvailableSeats === 0 ? 'Occupied' : 'Available';
                await new Promise((res, rej) => {
                    const query = 'UPDATE Rooms SET available_seats = ?, status = ? WHERE ID = ?';
                    connection.query(query, [newAvailableSeats, newStatus, roomIdInt], (err, result) => {
                        if (err) return rej(new Error("Li cp nht phng sau khi t: " + err.message));
                        if (result.affectedRows === 0) return rej(new Error("Li khng cp nht c phng."));
                        res(result);
                    });
                });

                // 9. Commit Transaction
                connection.commit((commitErr) => {
                    if (commitErr) {
                        console.error("Transaction Error: Commit add failed:", commitErr);
                        return connection.rollback(() => reject(new Error("Li xc nhn giao dch t phng.")));
                    }
                    resolve({ bookingId: bookingResult.insertId, message: "t phng thnh cng! Trng thi:  xc nhn." });
                });

            } catch (error) {
                // 10. Rollback Transaction
                console.error("Service Error: Processing booking:", error);
                connection.rollback(() => reject(error)); // Reject vi li gc
            }
        });
    });
};

/**
 * Hy mt lt t phng  xc nhn ('Confirmed') v cng li s ch trng.
 * @param {number} bookingId - ID ca lt t phng cn hy.
 * @returns {Promise<{message: string}>}
 */
const cancelBooking = (bookingId) => {
    return new Promise((resolve, reject) => {
        const bookingIdInt = parseInt(bookingId, 10);
        if (isNaN(bookingIdInt)) return reject(new Error("Booking ID khng hp l."));

        connection.beginTransaction(async (transactionErr) => {
            if (transactionErr) {
                console.error("Transaction Error: Begin cancel failed:", transactionErr);
                return reject(new Error("Khng th bt u giao dch hy."));
            }

            try {
                // 1. Ly booking info & lock (id, room_id, status, booked_seats)
                const bookingInfo = await new Promise((res, rej) => {
                    const query = `SELECT id, room_id, status, booked_seats FROM Bookings WHERE id = ? FOR UPDATE`;
                    connection.query(query, [bookingIdInt], (err, results) => {
                        if (err) return rej(new Error("Li ly thng tin t phng: " + err.message));
                        if (results.length === 0) return rej(new Error(`Khng tm thy t phng ID: ${bookingIdInt}.`));
                        res(results[0]);
                    });
                });

                // 2. Kim tra trng thi hp l ('Confirmed')
                if (bookingInfo.status !== 'Confirmed') {
                    throw new Error(`Ch hy c t phng 'Confirmed'. Trng thi hin ti: '${bookingInfo.status}'.`);
                }

                // 3. Ly room info & lock (ID, capacity, available_seats, status)
                const roomInfo = await new Promise((res, rej) => {
                    const query = `SELECT ID, capacity, available_seats, status FROM Rooms WHERE ID = ? FOR UPDATE`;
                    connection.query(query, [bookingInfo.room_id], (err, results) => {
                         if (err) return rej(new Error("Li ly thng tin phng: " + err.message));
                         if (results.length === 0) return rej(new Error(`Li d liu: Phng ID ${bookingInfo.room_id} khng tn ti.`));
                         res(results[0]);
                    });
                });

                // 4. Cp nht booking status -> 'Cancelled'
                await new Promise((res, rej) => {
                    const query = 'UPDATE Bookings SET status = ? WHERE id = ?';
                    connection.query(query, ['Cancelled', bookingIdInt], (err, result) => {
                         if (err) return rej(new Error("Li cp nht trng thi booking: " + err.message));
                         if (result.affectedRows === 0) return rej(new Error("Li khng cp nht c booking."));
                         res(result);
                    });
                });

                // 5. Cp nht li s ch trng v trng thi phng
                let newAvailableSeats = roomInfo.available_seats;
                let newRoomStatus = roomInfo.status;
                const seatsToAddBack = bookingInfo.booked_seats || 0;
                let roomNeedsUpdate = false;

                if (seatsToAddBack > 0 && roomInfo.status !== 'Maintenance') {
                    newAvailableSeats = roomInfo.available_seats + seatsToAddBack;
                    newAvailableSeats = Math.min(newAvailableSeats, roomInfo.capacity);
                    if (roomInfo.status === 'Occupied' && newAvailableSeats > 0) {
                        newRoomStatus = 'Available';
                    }
                    if (newAvailableSeats !== roomInfo.available_seats || newRoomStatus !== roomInfo.status) {
                        roomNeedsUpdate = true;
                    }
                } else { /* Log warnings if needed */ }

                if (roomNeedsUpdate) {
                    await new Promise((res, rej) => {
                        const query = 'UPDATE Rooms SET available_seats = ?, status = ? WHERE ID = ?';
                        connection.query(query, [newAvailableSeats, newRoomStatus, roomInfo.ID], (err, result) => {
                            if (err) return rej(new Error("Li cp nht phng sau khi hy: " + err.message));
                            if (result.affectedRows === 0) return rej(new Error("Li khng cp nht c phng sau hy."));
                            res(result);
                        });
                    });
                }

                // 6. Commit
                connection.commit((commitErr) => {
                    if (commitErr) {
                        console.error("Transaction Error: Commit cancel failed:", commitErr);
                        return connection.rollback(() => reject(new Error("Li xc nhn giao dch hy.")));
                    }
                    resolve({ message: `Hy lt t phng ID ${bookingIdInt} thnh cng.` });
                });

            } catch (error) {
                // 7. Rollback
                console.error(`Service Error: Cancelling booking ID ${bookingIdInt}:`, error);
                connection.rollback(() => reject(error));
            }
        });
    });
};

// --- Hm cho Cron Job (C th t  file ring) ---

/**
 * Tc v nh k: Hon thnh cc t phng  ht hn ('Confirmed' -> 'Completed')
 * v cng li ch trng cho phng (c n v nhm).
 */
const processCompletedBookings = () => {
    return new Promise(async (resolve, reject) => {
        const functionName = 'processCompletedBookings';
        console.log(`[${new Date().toISOString()}] Running job: ${functionName}...`);
        let completedCount = 0;
        let roomUpdateCount = 0;
        let failedCount = 0;

        try {
            // 1. Tm cc booking 'Confirmed'  ht hn
            const nowUTC = new Date();
            const nowMySQLFormatUTC = nowUTC.toISOString().slice(0, 19).replace('T', ' ');

            const findQuery = `
                SELECT id, room_id, booked_seats, status AS current_status
                FROM Bookings
                WHERE status = 'Confirmed' AND CONCAT(Day, ' ', end_time) < ?`;

            const bookingsToComplete = await new Promise((res, rej) => {
                connection.query(findQuery, [nowMySQLFormatUTC], (err, results) => {
                    if (err) return rej(new Error(`[${functionName}] Li tm booking ht hn: ${err.message}`));
                    res(results);
                });
            });

            if (bookingsToComplete.length === 0) {
                console.log(`[${functionName}] No bookings to complete.`);
                return resolve({ completed: 0, room_updates: 0, failed: 0, message: "Khng c t phng no cn hon thnh." });
            }
            console.log(`[${functionName}] Found ${bookingsToComplete.length} bookings to process.`);

            // 2. Lp v x l tng booking trong transaction ring
            for (const booking of bookingsToComplete) {
                await new Promise((res_inner) => {
                    connection.beginTransaction(async (transactionErr) => {
                        if (transactionErr) {
                            console.error(`[${functionName}] TX Begin Error (Booking ${booking.id}):`, transactionErr);
                            failedCount++; return res_inner();
                        }

                        try {
                            // Ly phng & lock
                            const roomInfo = await new Promise((res_room, rej_room) => {
                                const q = 'SELECT ID, capacity, available_seats, status FROM Rooms WHERE ID = ? FOR UPDATE';
                                connection.query(q, [booking.room_id], (err, results) => {
                                     if (err || results.length === 0) return rej_room(new Error(`[${functionName}] Li/Khng tm thy phng ${booking.room_id} (Booking ${booking.id}). ${err?.message || ''}`));
                                     res_room(results[0]);
                                });
                            });

                            // Cp nht booking -> 'Completed'
                            let bookingUpdated = false;
                            await new Promise((res_b, rej_b) => {
                                const q = 'UPDATE Bookings SET status = ? WHERE id = ? AND status = ?';
                                connection.query(q, ['Completed', booking.id, 'Confirmed'], (err, result) => {
                                    if (err) return rej_b(new Error(`[${functionName}] Li cp nht booking ${booking.id}: ${err.message}`));
                                    if (result.affectedRows > 0) { bookingUpdated = true; completedCount++; }
                                    else console.warn(`[${functionName}] Booking ${booking.id} khng cn 'Confirmed' khi x l.`);
                                    res_b(result);
                                });
                            });

                            // Nu booking c cp nht -> Cp nht phng
                            if (bookingUpdated) {
                                let newAvailableSeats = roomInfo.available_seats;
                                let newRoomStatus = roomInfo.status;
                                const seatsToAddBack = booking.booked_seats || 0;
                                let roomNeedsUpdate = false;

                                if (seatsToAddBack > 0 && roomInfo.status !== 'Maintenance') {
                                    newAvailableSeats = roomInfo.available_seats + seatsToAddBack;
                                    newAvailableSeats = Math.min(newAvailableSeats, roomInfo.capacity);
                                    if (roomInfo.status === 'Occupied' && newAvailableSeats > 0) newRoomStatus = 'Available';
                                    if (newAvailableSeats !== roomInfo.available_seats || newRoomStatus !== roomInfo.status) roomNeedsUpdate = true;
                                }

                                if (roomNeedsUpdate) {
                                    await new Promise((res_r, rej_r) => {
                                        const q = 'UPDATE Rooms SET available_seats = ?, status = ? WHERE ID = ?';
                                        connection.query(q, [newAvailableSeats, newRoomStatus, roomInfo.ID], (err, result) => {
                                            if (err) return rej_r(new Error(`[${functionName}] Li cp nht phng ${roomInfo.ID}: ${err.message}`));
                                            if (result.affectedRows === 0) return rej_r(new Error(`[${functionName}] Li khng cp nht c phng ${roomInfo.ID}.`));
                                            roomUpdateCount++;
                                            res_r(result);
                                        });
                                    });
                                }
                            } // end if(bookingUpdated)

                            // Commit
                            connection.commit((commitErr) => {
                                if (commitErr) {
                                    console.error(`[${functionName}] TX Commit Error (Booking ${booking.id}):`, commitErr);
                                    connection.rollback(() => { if (bookingUpdated) completedCount--; failedCount++; res_inner(); });
                                } else {
                                    if (bookingUpdated) console.log(`[${functionName}] Booking ${booking.id} -> Completed.`);
                                    res_inner();
                                }
                            });

                        } catch (error) {
                            console.error(`[${functionName}] Error processing Booking ${booking.id}:`, error);
                            connection.rollback(() => { failedCount++; res_inner(); });
                        }
                    }); // End beginTransaction
                }); // End new Promise wrapper
            } // End for loop

            resolve({ completed: completedCount, room_updates: roomUpdateCount, failed: failedCount, message: `Hon thnh ${completedCount}, cp nht ${roomUpdateCount} phng, ${failedCount} li.` });

        } catch (error) {
            console.error(`[${functionName}] Job Error:`, error);
            reject(error);
        }
    });
};

const bookNow = ({ roomId, mssv, duration_minutes, number_of_attendees }) => {
    return new Promise((resolve, reject) => {
        const functionName = 'bookNow';
        const roomIdInt = parseInt(roomId, 10);

        if (isNaN(roomIdInt)) return reject(new Error("Room ID khng hp l."));
        if (!mssv) return reject(new Error("Thiu thng tin ngi dng (MSSV)."));

        // Chuyn i duration_minutes v number_of_attendees thnh s hoc null nu khng hp l/khng cung cp
        const requestedDuration = duration_minutes ? parseInt(duration_minutes, 10) : null;
        const requestedAttendees = number_of_attendees ? parseInt(number_of_attendees, 10) : null;

        connection.beginTransaction(async (transactionErr) => {
            if (transactionErr) {
                console.error(`[${functionName}] TX Begin Error:`, transactionErr);
                return reject(new Error("Li khi bt u giao dch t phng ngay."));
            }

            try {
                // 1. Ly thng tin phng v kha bn ghi
                const roomInfo = await new Promise((res, rej) => {
                    const query = 'SELECT ID, capacity, location, status, available_seats, room_type FROM Rooms WHERE ID = ? FOR UPDATE';
                    connection.query(query, [roomIdInt], (err, results) => {
                         if (err) return rej(new Error(`[${functionName}] Li ly phng ${roomIdInt}: ${err.message}`));
                         if (results.length === 0) return rej(new Error(`[${functionName}] Khng tm thy phng ID: ${roomIdInt}.`));
                         res(results[0]);
                    });
                });

                // 2. Kim tra trng thi phng
                if (roomInfo.status !== 'Available') {
                    throw new Error(`Phng ${roomInfo.location} hin khng trng (Trng thi: ${roomInfo.status}). Khng th t ngay.`);
                }

                // 3. Xc nh thi gian t v s ngi tham gia cui cng (p dng mc nh nu cn)
                let effective_duration_minutes;
                let seatsToDecrement;

                if (roomInfo.room_type === 'single') {
                    // Phng n: Dng duration ngi dng cung cp hoc mc nh 60 pht
                    effective_duration_minutes = (requestedDuration && requestedDuration > 0) ? requestedDuration : DEFAULT_BOOKING_DURATION_MINUTES;
                    seatsToDecrement = DEFAULT_SINGLE_ATTENDEES; // Lun l 1 cho phng n
                } else if (roomInfo.room_type === 'group') {
                    // Phng nhm: Dng duration ngi dng cung cp hoc mc nh 120 pht
                    effective_duration_minutes = (requestedDuration && requestedDuration > 0) ? requestedDuration : DEFAULT_DURATION_QUICK_BOOK_MINUTES; // Mc nh 120 pht

                    // Phng nhm: Dng s ngi dng cung cp hoc mc nh 6 ngi
                    let attendees = (requestedAttendees && requestedAttendees > 0) ? requestedAttendees : DEFAULT_GROUP_ATTENDEES; // Mc nh 6 ngi

                    // Kim tra sc cha
                    if (attendees > roomInfo.capacity) {
                        throw new Error(`S lng (${attendees}) vt qu sc cha (${roomInfo.capacity}) ca phng nhm.`);
                    }
                    seatsToDecrement = attendees;
                } else {
                    throw new Error(`[${functionName}] Loi phng khng xc nh: ${roomInfo.room_type}`);
                }

                // Kim tra li nu duration_minutes sau khi xc nh khng hp l (d logic trn  x l)
                 if (isNaN(effective_duration_minutes) || effective_duration_minutes <= 0) {
                    return reject(new Error("Thi gian t phng cui cng khng hp l."));
                 }

                // 4. Tnh ton thi gian bt u/kt thc
                const now = new Date();
                const startTime = now;
                const endTime = new Date(now.getTime() + effective_duration_minutes * 60000);

                const bookingDate = format(startTime, 'yyyy-MM-dd');
                const startTimeStr = format(startTime, 'HH:mm:ss');
                const endTimeStr = format(endTime, 'HH:mm:ss');


                // 5. Kim tra ch trng
                if (roomInfo.available_seats < seatsToDecrement) {
                    throw new Error(`Khng  ch trng. Phng ${roomInfo.location} ch cn ${roomInfo.available_seats} ch (cn ${seatsToDecrement}).`);
                }

                // 6. Kim tra xung t thi gian
                const timeSlotCheck = await checkBookingTimeSlot(roomIdInt, bookingDate, startTimeStr, endTimeStr);
                if (!timeSlotCheck.available) {
                    throw new Error("Rt tic,  c lch t khc trng vi khong thi gian ny.");
                }

                // 7. To bn ghi t phng
                const bookingResult = await new Promise((res, rej) => {
                    const query = `INSERT INTO Bookings (room_id, mssv, Day, start_time, end_time, status, booked_seats) VALUES (?, ?, ?, ?, ?, ?, ?)`;
                    const values = [roomIdInt, mssv, bookingDate, startTimeStr, endTimeStr, 'Confirmed', seatsToDecrement];
                    connection.query(query, values, (err, result) => {
                         if (err) {
                             if (err.code === 'ER_DUP_ENTRY') return rej(new Error("Li: C v  c mt t phng trng khp."));
                             return rej(new Error(`[${functionName}] Li DB khi to booking: ${err.message}`));
                         }
                         if (!result || !result.insertId) return rej(new Error(`[${functionName}] Khng th to t phng.`));
                         res(result);
                    });
                });

                // 8. Cp nht s ch v trng thi phng
                const newAvailableSeats = roomInfo.available_seats - seatsToDecrement;
                const newStatus = newAvailableSeats <= 0 ? 'Occupied' : 'Available'; // <= 0  an ton
                await new Promise((res, rej) => {
                    const query = 'UPDATE Rooms SET available_seats = ?, status = ? WHERE ID = ?';
                    connection.query(query, [newAvailableSeats, newStatus, roomIdInt], (err, result) => {
                        if (err) return rej(new Error(`[${functionName}] Li cp nht phng ${roomIdInt}: ${err.message}`));
                        if (result.affectedRows === 0) return rej(new Error(`[${functionName}] Li khng cp nht c phng ${roomIdInt}.`));
                        res(result);
                    });
                });

                // 9. Commit transaction
                connection.commit((commitErr) => {
                    if (commitErr) {
                        console.error(`[${functionName}] TX Commit Error:`, commitErr);
                        return connection.rollback(() => reject(new Error("Li khi xc nhn giao dch t phng ngay.")));
                    }
                    resolve({
                        bookingId: bookingResult.insertId,
                        message: `t phng ${roomInfo.location} thnh cng!`,
                        startTime: startTimeStr,
                        endTime: endTimeStr,
                        date: bookingDate,
                        attendees: seatsToDecrement, // Tr v s ngi thc t  t
                        duration_minutes: effective_duration_minutes // Tr v thi gian thc t  t
                    });
                });

            } catch (error) {
                console.error(`[${functionName}] Error during transaction:`, error);
                connection.rollback(() => reject(error)); // Reject vi li gc  controller x l
            }
        });
    });
};



// --- Exports ---
module.exports = {
    getBookingList,
    addBooking,
    checkBookingTimeSlot,
    cancelBooking,
    processCompletedBookings,
    bookNow
};

